	private void insertEvent(int i, String json, JSONObject eventMapping, List<Map<String, Object>> event, String...replaceDateFields) {
		System.out.println("inserting event "+i);
		
		DateTime evdate = i%3==0?DateTime.now().minusMonths(i/(120-new Random().nextInt(12))):DateTime.now().minusMonths(i/(110-new Random().nextInt(6)));
		JSONObject ev = new JSONObject(json);
		ev.put("eventDate", evdate);
		ev.put("_id", ev.getString("_id")+i+new Random().nextInt(1000)+ev.getLong("serverVersion")+new Random().nextInt(1000));
		ev.put("baseEntityId", ev.getString("baseEntityId")+i);
		
		if(replaceDateFields != null) {
			replaceDateObs(ev.getJSONArray("obs"), evdate, replaceDateFields);
		}
		
		Map<String, Object> persistable = DatabaseUtils.convertToDatabaseObject(ev, eventMapping, event);
		
		cloudantDb.save(new JsonParser().parse(ev.toString()));

		// dataDao.insert("event", persistable);
	}
	
	public void replaceDateObs(JSONArray obs, DateTime eventDate, String...fields) {
		for (String f : fields) {
			String[] fd = f.split(":");
			for (int i = 0; i < obs.length(); i++) {
				JSONObject jo = obs.getJSONObject(i);
				if(jo.getString("formSubmissionField").equalsIgnoreCase(fd[0])) {
					jo.getJSONArray("values").remove(0);
					jo.getJSONArray("values").put(fd.length>1?new DateTime(eventDate).plusDays(Integer.parseInt(fd[1])).toString("yyyy-MM-dd"):eventDate.toString("yyyy-MM-dd"));
				}
			}
		}
	}
	
	public void fillName(int i, JSONObject client) {
		try {
			String [] names = new String[] {"Talat", "Rafat", "Najam", "Rasam", "Aman" ,"Anaam" ,"Sibghat" ,"Rehmat", "Nemat", "Naseem"};
			client.put("firstName", names[new Random().nextInt(10)]);
			client.put("lastName", i%3 == 0 ? "Hayat" : "Samar");
		}
		catch (Exception e) {}
	}
